import React from 'react';
import './App.css'
import AcmHeader from './components/acmheader';
//import MovieList from './components/movielist';
//import Movie from './components/movie';
import Authentication from './components/authentication';
import {HashRouter, Route} from 'react-router-dom';
import { Provider } from 'react-redux';
import store from './stores/store';
import Lottie from 'react-lottie';
import animationData from './lotties/animation';
import { TypeAnimation } from 'react-type-animation';
import ReactDOMServer from 'react-dom/server';

function App() {
  const defaultOptions = {
    loop: true,
    autoplay: true,
    animationData: animationData,
    rendererSettings: {
      preserveAspectRatio: "xMidYMid slice"
    }
  };

  const gradientStyle = {
  backgroundImage: 'linear-gradient(to right, var(--acm-turquoise), var(--acm-sky), var(--acm-blue), var(--acm-bluer), var(--acm-blue),  var(--acm-blue), var(--acm-purple), var(--acm-purple), var(--acm-pink), var(--acm-red))',
  position: 'absolute',
  left: '0',
  width: '100%',
  height: '2px' // Adjust the height as needed
};

const spanRef = useRef(null);
  const [sequence, setSequence] = useState('');

  useEffect(() => {
    if (spanRef.current) {
      // Extract inner HTML content of the span element
      const innerHTML = spanRef.current.innerHTML;
      setSequence(innerHTML);
    }
  }, []);

  return (
    <div>
      <Provider store={store}>
        <HashRouter>
          <AcmHeader />
          <body className="black-background">
            <div className="welcome">
              <h1 className="acm-heavier size-xl">
                We are the largest <br />
                <span ref={spanRef} className="acm-multi-color">Computer Science</span>
                <TypeAnimation sequence={sequence} speed={50} repeat={Infinity}/>
                <br />
                community at CU Denver <br />
              </h1> 
            </div>
            <div className="lottie-container">
              <Lottie
                className="lottie"
                options={defaultOptions}
                width={"80%"}
              />
              <div className="gradient-line"></div> {/* Gradient line */}
            </div>
          </body>
          <Route exact path="/signin" render={()=><Authentication />}/>
        </HashRouter>
      </Provider>
    </div>
  );

}

export default App;

