import React from 'react';
import './App.css'
import AcmHeader from './components/acmheader';
//import MovieList from './components/movielist';
//import Movie from './components/movie';
import Authentication from './components/authentication';
import {HashRouter, Route} from 'react-router-dom';
import { Provider } from 'react-redux';
import store from './stores/store';
import Lottie from 'react-lottie';
import animationData from './lotties/animation';

function App() {
  const defaultOptions = {
    loop: true,
    autoplay: true,
    animationData: animationData,
    rendererSettings: {
      preserveAspectRatio: "xMidYMid slice"
    }
  };

  const gradientStyle = {
  backgroundImage: 'linear-gradient(to right, var(--acm-turquoise), var(--acm-sky), var(--acm-blue), var(--acm-bluer), var(--acm-blue),  var(--acm-blue), var(--acm-purple), var(--acm-purple), var(--acm-pink), var(--acm-red))',
  position: 'absolute',
  bottom: '0',
  left: '0',
  width: '100%',
  height: '2px' // Adjust the height as needed
};

  return (
    <div>
      <Provider store={store}>
        <HashRouter>
            <AcmHeader />
                <body>
                  <h1 className="acm-heavier size-xl welcome">
                      We are the largest <br />
                      <span className="acm-multi-color">Computer Science</span><br />
                      community at CU Denver <br />
                  </h1>
                  <div style={gradientStyle}></div>
                  <div className="lottie">
                      <Lottie 
                        options={defaultOptions}
                        width={"100%"}
                        style={{ backgroundColor: 'transparent' }}
                        />
                  </div>
                </body>
            <Route exact path="/signin" render={()=><Authentication />}/>
        </HashRouter>
      </Provider>
    </div>
  );
}

export default App;

