import React, { Component } from 'react';
import { submitLogin } from '../actions/authActions';
import { connect } from 'react-redux';
import { Form, Button } from 'react-bootstrap';
import '../style/login.css'
import '../style/colors.css'
import '../style/fonts.css'


class Login extends Component {

    constructor(props) {
        super(props);
        this.updateDetails = this.updateDetails.bind(this);
        this.login = this.login.bind(this);

        this.state = {
            details:{
                username: '',
                password: ''
            }
        };
    }

    updateDetails(event){
        let updateDetails = Object.assign({}, this.state.details);

        updateDetails[event.target.id] = event.target.value;
        this.setState({
            details: updateDetails
        });
    }

    login() {
        const {dispatch} = this.props;
        dispatch(submitLogin(this.state.details));
    }

    componentDidMount() {
        // Initialize Apple Sign In
        if (window.AppleID) {
            window.AppleID.auth.init({
                clientId : '[CLIENT_ID]',
                scope : '[SCOPES]',
                redirectURI : '[REDIRECT_URI]',
                state : '[STATE]',
                nonce : '[NONCE]',
                usePopup : true
            });
        } else {
            console.error("Apple Sign In not available.");
        }
    }


    render(){
        return (
            <div className="login-style">
                <h1 className="acm-multi-color">Login to your account</h1><br /><br />
                <Form className='form-horizontal'>
                    <Form.Group controlId="username">
                        <Form.Control onChange={this.updateDetails} value={this.state.details.username} 
                        style={{textAlign: 'center'}} type="email" placeholder="Enter email" />
                    </Form.Group>

                    <Form.Group controlId="password">
                        <Form.Control onChange={this.updateDetails} value={this.state.details.password}  
                        style={{textAlign: 'center'}} type="password" placeholder="Password" />
                    </Form.Group>
                    <Button className="acm-multi-color" onClick={this.login}>Sign in</Button>
                    <br /><br />  
                    <div className="container">
                    <div id="appleid-signin" data-color="black" data-border="false" data-type="sign in"></div>
                    </div>
                 </Form>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
    }
}

export default connect(mapStateToProps)(Login);
