import React, { Component } from 'react';
import { fetchBlogPosts } from "../actions/blogActions";
import { setBlogPost } from "../actions/blogActions";
import {connect} from 'react-redux';
import {Image, Nav} from 'react-bootstrap';
import {LinkContainer} from 'react-router-bootstrap';
import { Stack, Button, Card } from 'react-bootstrap';

class Blog extends Component {
    constructor(props) {
        super(props);
        this.handleSelect = this.handleSelect.bind(this);
    }

    componentDidMount() {
        const {dispatch} = this.props;
        dispatch(fetchBlogPosts());
    }

    handleSelect(selectedIndex, e) {
        const {dispatch} = this.props;
        dispatch(setBlogPost(this.props.blogPosts[selectedIndex]));
    }

    handleClick = (blogPost) => {
        const {dispatch} = this.props;
        dispatch(setBlogPost(blogPost));
    }

    render() {
       return (
            <Stack gap={2} className="stack-layout">
                {blogPosts.map(post => 
                    <Card key={post._id}>
                        <Card.Body> Test title.</Card.Body>
                        <Card.Body> This is a test.</Card.Body>
                    </Card>
                    )}
            </Stack>
       )
    }
}

const mapStateToProps = state => {
    return {
        blogPosts: state.blogPost.blogPosts
    }
}

export default connect(mapStateToProps)(BlogPosts);
